    if (currentMessage !==""){
      const messageData = {
        room: room,
        aurthor:username,
        message:currentMessage,
        time:new Date(Date.now()).getHours()+":"+Date(Date.now()).ge
      }
    }
  }
  return (
    <div className="chat-window">
      <div className="chat-header">
        <p>Live Chat</p>
      </div>
